function runMigration(mig, next) {
    console.log(`MIGRATION START: ${mig.name}`)
    return new Promise((resolve, reject) => {
        mig()
            .then(() => {
                console.log(`MIGRATION END: ${mig.name}`)
                if (next) {
                    runMigration(next)
                }
                else {
                    resolve()
                }
            })
            .catch((e) => {
                reject(e)
            })
    })
}

